function init(){$('.apple, .orange').draggable({revert:'invalid'})}function dragAndDrop(){$('.kastrull').droppable({accept:'.apple',drop:function(a,b){b.draggable.is('.apple')?($('.grattis').show(),$('.apple').hide()):b.draggable.draggable('option','revert',!0)}})}$(document).ready(function(){init(),dragAndDrop()});